import { useState } from "react";
import "./App.css";

function App() {
  const [listaAtividades, setListaAtividades] = useState([
    {
      Id: 1,
      Descricao: "Primeira Atividade",
    },
    {
      Id: 2,
      Descricao: "Segunda Atividade",
    },
  ]);

  function addAtividade(e) {
    e.preventDefault();
    const atividade = {
      Id: document.getElementById("id").value,
      Descricao: document.getElementById("descricao").value,
    };
    setListaAtividades([...listaAtividades, { ...atividade }]);
  }

  return (
    <>
      <form className="row g-3">
        <div className="colmd-6">
          <label className="form-label" htmlFor="id">Id da atividade</label>
          <input className="form-control" id="id" type="text" placeholder="Id" />
        </div>
        <div className="colmd-6">
          <label className="form-label" htmlFor="descricao">Descrição da atividade</label>
          <input className="form-control" id="descricao" type="text" placeholder="descricao" />
        </div>
        <button className="btn btn-outline-success" onClick={addAtividade}>+ Atividades</button>
      </form>

      <div className="mt-3">
        <ul className="list-group">
          {listaAtividades.map((ativ) => (
            <li key={ativ.Id} className="list-group-item">
              {ativ.Descricao}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}

export default App;
