import { useState } from "react";
import "./App.css";
import Atividade from "./components/Atividade";
import AtividadeForm from "./components/AtividadeForm";

function App() {
  const [listaAtividades, setListaAtividades] = useState([
    {
      Id: 1,
      Descricao: "Primeira Atividade",
      Prioridade: "1",
      Titulo: "Título",
    },
    {
      Id: 2,
      Descricao: "Segunda Atividade",
      Prioridade: "1",
      Titulo: "Título",
    },
  ]);

  function addAtividade(e) {
    e.preventDefault();
    const atividade = {
      Id: document.getElementById("id").value,
      Descricao: document.getElementById("descricao").value,
      Titulo: document.getElementById("titulo").value,
      Prioridade: document.getElementById("prioridade").value,
    };
    setListaAtividades([...listaAtividades, { ...atividade }]);
  }

  function deletarAtividade(param) {
    const atividadesFiltradas = listaAtividades.filter((p) => p.Id !== param);

    setListaAtividades([...atividadesFiltradas]);
  }

  function prioridadeAtividade(param) {
    switch (param) {
      case "1":
        return "Baixa";
      case "2":
        return "Normal";
      case "3":
        return "Alta";

      default:
        return "Não Definido";
    }
  }

  function prioridadeAtividadeStyle(param, icone) {
    switch (param) {
      case "1":
        return icone ? "smile" : "success";
      case "2":
        return icone ? "meh" : "secondary";
      case "3":
        return icone ? "frown" : "danger";

      default:
        return "dark";
    }
  }

  return (
    <>
      <AtividadeForm
        addAtividade={addAtividade}
        listaAtividades={listaAtividades}
      />

      <div className="mt-5">
        {listaAtividades.map((ativ) => (
          <Atividade
            ativ={ativ}
            prioridadeAtividadeStyle={prioridadeAtividadeStyle}
            prioridadeAtividade={prioridadeAtividade}
            deletarAtividade={deletarAtividade}
          />
        ))}
      </div>
    </>
  );
}

export default App;
